import{_ as s,c as o,o as a,j as e,a as t}from"./chunks/framework.DOnJscRE.js";const k=JSON.parse('{"title":"10 集群","description":"","frontmatter":{"title":"10 集群","author":"Herman","updateTime":"2024-08-11 21:34","desc":"集群","categories":"中间件","tags":"redis","outline":"deep"},"headers":[],"relativePath":"Notes/No2TechColumn/03 Redis/10 集群.md","filePath":"Notes/No2TechColumn/03 Redis/10 集群.md","lastUpdated":1764580348000}'),r={name:"Notes/No2TechColumn/03 Redis/10 集群.md"},n=e("h3",{id:"cap-原理",tabindex:"-1"},[t("CAP 原理 "),e("a",{class:"header-anchor",href:"#cap-原理","aria-label":'Permalink to "CAP 原理"'},"​")],-1),d=e("p",null,"CAP是分布式存储的理论基石， 一致性(C) 、 可用性(A)、 分区容忍性(P); 分布式的节点往往都是分布在不同的机器上，这意味着必然会有网络断开的风险，这个场景叫做网络分区。",-1),i=e("p",null,"在网络分区发生时，两个分布式节点之间无法进行通信，我们对一个节点进行的修改操作将无法同步到另外一个节点，所以数据的「一致性」将无法满足，因为两个分布式节点的数据不再保持一致。除非我们牺牲「可用性」，也就是暂停分布式节点服务，在网络分区发生时，不再提供修改数据的功能，直到网络状况完全恢复正常再继续对外提供服务。",-1),c=e("blockquote",null,[e("p",null,"一句话概括 CAP 原理就是——网络分区发生时，一致性和可用性两难全。")],-1),l=e("p",null,"redis的主从同步是异步的，所以redis的分布式系统并不满足一致性，即使主从网络出现了问题，主节点依旧可以对外提供服务，所以redis的分布式系统是满足了 AP； 当从节点网络恢复后，会努力追赶主节点，最终会和主节点的状态一致，所以redis采用的是最终一致性。",-1),_=e("p",null,[t("原文链接: "),e("a",{href:"http://herman7z.site",target:"_blank",rel:"noreferrer"},"http://herman7z.site")],-1),h=[n,d,i,c,l,_];function p(u,m,f,N,P,C){return a(),o("div",null,h)}const x=s(r,[["render",p]]);export{k as __pageData,x as default};
